import React, { Component } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import { connect } from 'react-redux';
import {withRouter} from 'react-router'

class Edit extends Component {
    state = {
        movie: {
            title: '',
            description: '',
            poster: '',
        }
    }
    componentDidMount() {
        this.plantInfo();
    }
    plantInfo = () =>{
        {this.props.reduxState.infoMovie.map((movieInfo) => {
            this.setState({
                movie: {
                    title: movieInfo.title,
                    description: movieInfo.description,
                    poster: movieInfo.poster,
                }
            })
        })}
    }
    handleChange = (event, propertyName) => {
        this.setState({
          movie: {
            ...this.state.movie,
           [propertyName]: event.target.value,
          }
        })
    }
    handleSubmit = () =>{
        this.props.dispatch({ type: 'UPDATE_PLANTS', payload: this.state.plant })
        this.props.history.push(`/plant/${this.state.plant.id}`)
        console.log('In submit');
    }

    render() {
        return (
            <div>
                <p>{JSON.stringify(this.state.movie)}</p>
                <img src = {this.state.movie.poster}></img>
                <p>Title:</p>
                <input style={{size: 30, width: 100}} onChange = {(event) => this.handleChange(event, 'title')} value = {this.state.movie.title}></input>
                <br/>
                <p>Description:</p>
                <textarea cols="40" rows="5" onChange = {(event) => this.handleChange(event, 'description')} value = {this.state.movie.description}></textarea>
                <br/>
            </div>
        )
    }
}
const mapStateToProps = reduxState => ({
    reduxState,
});

export default withRouter(connect(mapStateToProps)(Edit));
