import React, { Component } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import { connect } from 'react-redux';
import {withRouter} from 'react-router'

class Edit extends Component {
    state = {
        movie: {
            title: '',
            description: '',
        }
    }
    componentDidMount() {
        this.plantInfo();
    }
    plantInfo = () =>{
        {this.props.reduxState.infoMovie.map((movieInfo) => {
            this.setState({
                movie: {
                    title: movieInfo.title,
                    description: movieInfo.description,
                }
            })
        console.log('plantInfo', movieInfo);
        })}
    }
    handleChange = (event, propertyName) => {
        this.setState({
          movie: {
            ...this.state.movie,
           [propertyName]: event.target.value,
          }
        })
        console.log(this.state.plant)
    }
    handleSubmit = () =>{
        this.props.dispatch({ type: 'UPDATE_PLANTS', payload: this.state.plant })
        this.props.history.push(`/plant/${this.state.plant.id}`)
        console.log('In submit');
    }

    render() {
        return (
            <div>
                <img src = {this.props.reduxState.infoMovie.poster} />
                <p>Title:</p>
                <input size="35" onChange = {(event) => this.handleChange(event, 'name')} value = {this.state.movie.title}></input>
                <br/>
                <p>Description:</p>
                <input size="width: 350px, height: 100px;" onChange = {(event) => this.handleChange(event, 'kingdom')} value = {this.state.movie.description}></input>
                <br/>
            </div>
        )
    }
}
const mapStateToProps = reduxState => ({
    reduxState,
});

export default withRouter(connect(mapStateToProps)(Edit));
