import React, { Component } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import { connect } from 'react-redux';
import {withRouter} from 'react-router-dom';

class MovieItem extends Component {
    state = {
        movie: []
    }
    componentDidMount() {
        this.ID()
    }
    ID = () => {
        console.log(this.props.match.params.id)
        this.props.dispatch({ type: 'SET_INFO_MOVIES', payload: this.props.match.params.id })
    }

    render() {
        return (
            <Router>
                <p>{JSON.stringify(this.props.reduxState)}</p>
                <div >
                    <img src = {this.props.reduxState.infoMovie.poster}/>
                    <h3>{this.props.reduxState.infoMovie.title}</h3>
                    <p>{this.props.reduxState.infoMovie.description}</p>
                </div>
            </Router>
        );
    }
}

const mapStateToProps = reduxState => ({
    reduxState,
});

export default withRouter(connect(mapStateToProps)(MovieItem));
